<div id="main_content">	<h2>PENDAFTARAN</h2>	<div style="height: 924px;">		<form action="<?php echo $home_url."user.php?".$rand; ?>" id="formdaftar" name="formdaftar" method="post">			<table align="center" bgcolor="" border="0" cellpadding="0" width="600" style="margin: 0 auto;color: #ededed;">					<tbody>					<tr>						<td>							<p>Hi Gan !</p>							<p>Harap isi form berikut ini dengan data yang benar								<br/>								Pemenang akan di hubungi melalui email dan sms</p>							<br />							<input class="login" name="username" id="username" placeholder="Username" size="20" type="text" />							<br />							<br />							<input class="login number" name="nohp" id="nohp" placeholder="No Handphone" size="20" type="text" />							<br />							<br/>							<input class="login" name="password" id="password" placeholder="Password" size="20" type="password" />							<br />							<br/>							<input class="login" name="repassword" id="repassword" placeholder="Masukan Ulang Password" size="20" type="password" />							<br />							<br/>											<input class="login" name="email" id="email" placeholder="Email Anda" size="20" type="text" />							<br />							<br />											<br />											<p>								<span style="color:#FF0000;">* </span>								<span style="color:#FFD700;">Sebelum isi bagian ini, pastikan anda sudah mendaftar di website sponsor.									<br />									Dan harap di isi sesuai dengan ID yang di daftarkan.</span>							</p>								<br />										<input class="login" name="dewahokiuser" id="dewahokiuser" placeholder="ID DEWAHOKI *" size="20" type="text" />							&nbsp;							&nbsp;Dapatkan ID DewaHoki di <a href="http://dewahoki.us" target="_blank">sini</a>							<br />											<br />										<input class="login" name="jayabolauser" id="jayabolauser" placeholder="ID JAYABOLA *" size="20" type="text" />							&nbsp; 							&nbsp;Dapatkan ID JayaBola di <a href="http://dewahoki.us" target="_blank">sini</a>							<br />							<br />							<div id="captcha_num_container">								<span id="captcha_num1"><?php echo $num1; ?></span>								<span id="captcha_operator">+</span>								<span id="captcha_num2"><?php echo $num2; ?></span>								<span id="captcha_equation">=</span>							</div>							<input type="text" class="login" placeholder="Jawaban" name="captcha_code" id="captcha_code" size="5" maxlength="3" />							<!-- img src="<?php echo $home_url.'tools/captcha-images.php?rand='.$rand; ?>" id="capt" />								<input type="button" onClick="reload();" value='Reload image' />											<a href="#" onclick="document.getElementById('captcha').src = '<?php echo $home_url; ?>tools/securimage/securimage_show.php?' + Math.random(); return false" />								<img id="captcha" align="absmiddle" class="mtop20" src="<?php echo $home_url; ?>tools/securimage/securimage_show.php?rand=<?php echo $rand; ?>" alt="CAPTCHA Image" />							</a -->											<br/>											<br />											<input type="hidden" name="page" value="register" />											<input class="submit" type="submit" value="DAFTAR SEKARANG" />						</td>								</tr>						</tbody>			</table>			</form>		</div></div><script type="text/javascript">$(function() {	var counter = 0;	$('#formdaftar').submit(function() {		if($("#username").val() == ""){						alert('Username belum diisi.');			return false;		}		else if($("#username").val().length < 4 || $("#username").val().length > 8){			alert('Username minimal 4 karakter dan maksimal 8 karakter.');			return false;		}		else if($("#email").val() == ""){			alert('Email belum diisi.');						return false;				}				else if($("#nohp").val() == ""){			alert('No HP belum diisi.');						return false;				}		else if($("#password").val() == ""){			alert('Password belum diisi.');					return false;				}		else if($("#password").val() != $("#repassword").val()){			alert('Password dan konfirmasi password harus sama.');				return false;				}		else if($("#dewahokiuser").val() == ""){			alert('Username Dewahoki belum diisi.');				return false;				}		else if($("#jayabolauser").val() == "" ){			alert('Username Jayabola belum diisi.');				return false;				}		else if($("#captcha_code").val() != getCookie("num_captcha")){			counter++;			alert('Jawaban salah.');			if(counter == 3){				location.reload();			}			return false;		}	});});/* function reload(){	img = document.getElementById("capt");		img.src = window.location.origin + "/tools/captcha-image.php?rand_number=" + Math.random();} */</script>